import AppAction from './action';



let initialState = {
    loadingApp: false,
    loadingAppPopup: false,
    WardsDataaction: {
        loading: false,
        wardsdata: {},
        detail:{}
    },
    
};

const AppReducer = (state = initialState, action) =>
{
    switch (action.type) {
        case AppAction.LOADING_APP_START:
            return {
                ...state,
                loadingApp: true,
            };
        case AppAction.CLOSE_LOADING_APP:
            return {
                ...state,
                loadingApp: false,
            };
        case AppAction.LOADING_APP_POPUP_START:
            return {
                ...state,
                loadingAppPopup: true,
            };
        case AppAction.CLOSE_LOADING_APP_POPUP:
            return {
                ...state,
                loadingAppPopup: false,
            };
        case AppAction.FETCH_GET_WARDS:

            return {
                ...state,
                WardsDataaction: {
                    ...state.WardsDataaction,
                    loading: true,
                },
            };
        case AppAction.FETCH_GET_WARDS_SUCCESS:

            return {
                ...state,
                WardsDataaction: {
                    ...state.WardsDataaction,
                    wardsdata: action.payload,
                    loading: false,
                },
            };
           
        default:
            return {
                ...state,
            };
    }
};

export default AppReducer;
