import AppAction from './action';



let initialState = {
    loadingApp: false,
    loadingAppPopup: false,
    districtData: {
        loading: false,
        district: {}
        
    },
    
};

const AppReducer = (state = initialState, action) =>
{
    switch (action.type) {
        case AppAction.LOADING_APP_START:
            return {
                ...state,
                loadingApp: true,
            };
        case AppAction.CLOSE_LOADING_APP:
            return {
                ...state,
                loadingApp: false,
            };
        case AppAction.LOADING_APP_POPUP_START:
            return {
                ...state,
                loadingAppPopup: true,
            };
        case AppAction.CLOSE_LOADING_APP_POPUP:
            return {
                ...state,
                loadingAppPopup: false,
            };
        case AppAction.FETCH_GET_DISTRICT:

            return {
                ...state,
                districtData: {
                    ...state.districtData,
                    loading: true,
                },
            };
        case AppAction.FETCH_GET_DISTRICT_SUCCESS:
            console.log(distri.value);
          var searchinputddistrict =[]
          if(distri.value){
             searchinputddistrict = action.payload.filter((item,indexl)=>{
                console.log(item);
               let searchinput = distri.value!=""?item.name.toLowerCase().includes(distri.value.toLowerCase().trim()):true
               
               return searchinput
            }) 

            }else{
                searchinputddistrict =action.payload
            }
            return {
                ...state,
                districtData: {
                    ...state.districtData,
                    district: searchinputddistrict,
                    loading: false,
                },
            };
           

        default:
            return {
                ...state,
            };
    }
};

export default AppReducer;
