import AppAction from './action';



let initialState = {
    loadingApp: false,
    loadingAppPopup: false,
    codeDaTa: {
        loading: false,
        codeda: {}
    },
    
};

const AppReducer = (state = initialState, action) =>
{
    switch (action.type) {
        case AppAction.LOADING_APP_START:
            return {
                ...state,
                loadingApp: true,
            };
        case AppAction.CLOSE_LOADING_APP:
            return {
                ...state,
                loadingApp: false,
            };
        case AppAction.LOADING_APP_POPUP_START:
            return {
                ...state,
                loadingAppPopup: true,
            };
        case AppAction.CLOSE_LOADING_APP_POPUP:
            return {
                ...state,
                loadingAppPopup: false,
            };
        case AppAction.FETCH_GET_CODE:
           
            return {
                ...state,
                codeDaTa: {
                    ...state.codeDaTa,
                    loading: true,
                },
            };
        case AppAction.FETCH_GET_CODE_SUCCESS:
         
            // var codesearch =[]
            
            // if(code.value){
            //     codesearch = action.payload.filter((item,indexl)=>{
            //         let searchcode = code.value!=""?item.name.toLowerCase().includes(code.value.toLowerCase().trim()):true
            //         return searchcode
            //     })

            // }else{
            //     codesearch=action.payload
            // }
          
            return {
                ...state,
                codeDaTa: {
                    ...state.codeDaTa,
                    code: action.payload,
                    loading: false,
                },
            };
           

        default:
            return {
                ...state,
            };
    }
};

export default AppReducer;
