import AppAction from './action';



let initialState = {
    loadingApp: false,
    loadingAppPopup: false,
    sampleData: {
        loading: false,
        data: {}
    },
    
};

const AppReducer = (state = initialState, action) =>
{
    switch (action.type) {
        case AppAction.LOADING_APP_START:
            return {
                ...state,
                loadingApp: true,
            };
        case AppAction.CLOSE_LOADING_APP:
            return {
                ...state,
                loadingApp: false,
            };
        case AppAction.LOADING_APP_POPUP_START:
            return {
                ...state,
                loadingAppPopup: true,
            };
        case AppAction.CLOSE_LOADING_APP_POPUP:
            return {
                ...state,
                loadingAppPopup: false,
            };
        case AppAction.FETCH_SAMPLE_1:

            return {
                ...state,
                sampleData: {
                    ...state.sampleData,
                    loading: true,
                },
            };
        case AppAction.FETCH_SAMPLE_1_SUCCESS:

            return {
                ...state,
                sampleData: {
                    ...state.sampleData,
                    data: action.payload,
                    loading: false,
                },
            };
            // case AppAction.FETCH_GET_DATA:
            //     return{
            //         ...state,
            //             supplierData:{
            //                 ...state.supplierData,
            //                 loading:true,
            //             }
            //     };
                // case AppAction.FETCH_GET_DATA_SUCCESS:
                   
                //     return{
                //         ...state,
                //         supplierData:{
                //             ...state.supplierData,
                //             data:state.supplierData.data,
                //             loading:false,
                //         }
                //     };

        default:
            return {
                ...state,
            };
    }
};

export default AppReducer;
