import AppAction from './action';



let initialState = {
    loadingApp: false,
    loadingAppPopup: false,
    codeDaTa: {
        loading: false,
        codeda: {}
    },
    
};

const AppReducer = (state = initialState, action) =>
{
    switch (action.type) {
        case AppAction.LOADING_APP_START:
            return {
                ...state,
                loadingApp: true,
            };
        case AppAction.CLOSE_LOADING_APP:
            return {
                ...state,
                loadingApp: false,
            };
        case AppAction.LOADING_APP_POPUP_START:
            return {
                ...state,
                loadingAppPopup: true,
            };
        case AppAction.CLOSE_LOADING_APP_POPUP:
            return {
                ...state,
                loadingAppPopup: false,
            };
        case AppAction.FETCH_GET_CODE:
           
            return {
                ...state,
                codeDaTa: {
                    ...state.codeDaTa,
                    loading: true,
                },
            };
        case AppAction.FETCH_GET_CODE_SUCCESS:
          
           console.log(state);
            return {
                ...state,
                codeDaTa: {
                    ...state.codeDaTa,
                    codeda: action.payload,
                    loading: false,
                },
            };
           

        default:
            return {
                ...state,
            };
    }
};

export default AppReducer;
