import ApiConstants from 'adapter/ApiConstants';
import ApiOperation from 'adapter/ApiOperation';

const AppFactory = {
    fetchSample: async (data) => {


        //    let disti = data.payload.data

        return ApiOperation.request({
            url: ApiConstants.distrist,
            method: 'GET',
            district: data

        });
        // return

    },
    fetchgetdataID: (data) => {
       
        var iddistricit = []
      
            iddistricit = data.payload.distr.district.filter((item, indexl) => {
                if (data.payload.distr.search) {
                    console.log(item.name.toLowerCase().includes(data.payload.distr.search.toLowerCase().trim()));
                let searchdistri = data.payload.distr.search!=""?item.name.toLowerCase().includes(data.payload.distr.search.toLowerCase().trim()):true
                    return searchdistri
                }else{
                    return parseInt(item.province_id) == parseInt(data.payload.distr.idpro);
                }
                    
                
            })
            console.log(iddistricit);
        

        return {
            districtID: iddistricit
        };

    },
}

export default AppFactory
